import streamlit as st

st.title("BANGUN RUANG KERUCUT")
st.markdown("""
### Penjabaran :
- V = Volume
- LP = Luas Permukaan
- r = Jari-jari
- t = tinggi
- s = Gari Pelukis (Sisi Miring Kerucut)
- Ï€ = 3,14 atau 22/7
### Rumus:
""")
st.image("data:image/png;base64,", caption="Gambar ini diambil dari internet", use_container_width=True)

r = st.number_input("Masukan nilai jari-jari")
t = st.number_input("Masukan nilai tinggi")
v = 1/3 * (3.14 * r **2 *t)
v1 = 1/3 * (22/7 * r **2 *t)
st.header("Volume (3,14)")
st.subheader(v)
st.header("Volume (22/7)")

st.subheader(v1)
